import subprocess
import sys

python = sys.executable


def call_fuzzer(fn):
    cmd = python + ' bin/opfuzz "" ' + fn
    output = subprocess.getoutput(cmd)
    return output


fn = "examples/phi1.smt2"
out = call_fuzzer(fn)
print(out)
if "error: no solver specified" in out:
    exit(0)
exit(1)
